// Define viral topics that perform well
const viralTopics = [
  'space exploration discoveries',
  'historical mysteries solved',
  'scientific breakthroughs',
  'technology innovations',
  'ocean discoveries',
  'archaeological findings',
  'wildlife documentaries',
  'cultural traditions',
  'educational science',
  'interesting facts'
];

// Pick random topic or use current trending event
const randomTopic = viralTopics[Math.floor(Math.random() * viralTopics.length)];

// Generate unique run ID for debugging
const runId = 'run_' + new Date().getTime() + '_' + Math.random().toString(36).substr(2, 9);

const result = {
  Topic: randomTopic,
  Max_Results: 5,
  timestamp: new Date().toISOString(),
  runId: runId
};

// Cache this run start
console.log(`=== RUN ${runId} STARTED ===`);
console.log('Topic selected:', randomTopic);
console.log('Run data:', JSON.stringify(result, null, 2));

return { json: result };

// Enhanced content processing with error handling and caching
const runId = $node['Generate Viral Topic'].json.runId;
console.log(`=== RUN ${runId}: PROCESSING CONTENT ===`);

try {
  const results = $json.results || $json.data || $json || [];

  // Log raw PostCrawl response for debugging
  console.log('PostCrawl raw response type:', typeof results);
  console.log('PostCrawl raw response length:', Array.isArray(results) ? results.length : 'not array');
  console.log('PostCrawl sample data:', JSON.stringify(results).substring(0, 500) + '...');

  let processedContent = {
    hotPosts: [],
    viralComments: [],
    trendingHashtags: [],
    keyInsights: []
  };

  // Handle different response formats from PostCrawl
  let postsArray = [];
  if (Array.isArray(results)) {
    postsArray = results;
  } else if (results.posts && Array.isArray(results.posts)) {
    postsArray = results.posts;
  } else if (results.data && Array.isArray(results.data)) {
    postsArray = results.data;
  }

  console.log(`Processing ${postsArray.length} posts`);

  postsArray.slice(0, 5).forEach((item, index) => {
    if (item && typeof item === 'object') {
      // Clean and validate content
      const title = (item.title || item.text || 'Content Post').substring(0, 200);
      const content = (item.content || item.description || item.text || '').substring(0, 500);

      const post = {
        title: title,
        content: content,
        source: item.source || 'social_media',
        engagement: item.upvotes || item.likes || item.score || 0,
        url: item.url || '',
        timestamp: item.created_utc || item.timestamp || new Date().toISOString()
      };

      processedContent.hotPosts.push(post);
      console.log(`Post ${index + 1}:`, post.title.substring(0, 50) + '...');

      // Extract comments safely
      if (item.comments && Array.isArray(item.comments)) {
        item.comments.slice(0, 2).forEach(comment => {
          if (comment && (comment.text || comment.content)) {
            const commentText = (comment.text || comment.content).substring(0, 150);
            if (commentText.length > 10) {
              processedContent.viralComments.push(commentText);
            }
          }
        });
      }
    }
  });

  // Generate content summary for script creation
  const contentSummary = processedContent.hotPosts
    .filter(post => post.content.length > 20)
    .map(post => `${post.title}: ${post.content}`)
    .join('\n\n')
    .substring(0, 1000);

  // If no content found, create fallback
  if (processedContent.hotPosts.length === 0) {
    console.log('No posts found, using fallback content');
    processedContent.hotPosts.push({
      title: "Interesting Topic Discussion",
      content: "Recent discussions have brought up fascinating points about this topic that deserve exploration.",
      source: "general",
      engagement: 0
    });
  }

  const result = {
    originalTopic: $node['Generate Viral Topic'].json.Topic,
    processedContent: processedContent,
    contentSummary: contentSummary || "General discussion about the topic with various perspectives.",
    totalPosts: processedContent.hotPosts.length,
    hasContent: processedContent.hotPosts.length > 0,
    generatedAt: new Date().toISOString(),
    runId: runId,
    debug: {
      rawResponseType: typeof results,
      rawResponseKeys: Object.keys(results || {}),
      postsArrayLength: postsArray.length
    }
  };

  console.log(`Content processing complete: ${result.totalPosts} posts, ${result.processedContent.viralComments.length} comments`);
  console.log('Content summary length:', result.contentSummary.length);

  return { json: result };

} catch (error) {
  console.error(`RUN ${runId}: Error processing content:`, error);

  return {
    json: {
      originalTopic: $node['Generate Viral Topic'].json.Topic || "General Topic",
      processedContent: {
        hotPosts: [{
          title: "Discussion Topic",
          content: "Various perspectives and discussions around this interesting topic.",
          source: "fallback",
          engagement: 0
        }],
        viralComments: [],
        trendingHashtags: [],
        keyInsights: []
      },
      contentSummary: "General discussion about the topic with various perspectives.",
      totalPosts: 1,
      hasContent: true,
      error: error.message,
      generatedAt: new Date().toISOString(),
      runId: runId,
      debug: {
        errorOccurred: true,
        errorMessage: error.message
      }
    }
  };
}

// Create comprehensive video package with error handling
console.log('=== VIDEO PACKAGE CREATION ===');
console.log('Received data from merge:', JSON.stringify($json, null, 2));

try {
  // Try to get data from direct node references first, fallback to merged data
  const script = $node['Generate Viral Script'].json.message.content || 'No script available';
  const images = $node['Generate Content Images'].json.data || [];
  const audioData = $node['Generate Audio Narration'].json || {};
  const originalData = $node['Process Viral Content'].json;

  console.log('Script length:', script.length);
  console.log('Images count:', images.length);
  console.log('Audio data keys:', Object.keys(audioData));
  console.log('Original data keys:', Object.keys(originalData));

  // Extract title from script
  const lines = script.split('\n').filter(line => line.trim());
  const hookLine = lines[0] || 'Educational Content';
  const title = hookLine.replace(/[\"']/g, '').trim().substring(0, 50);

  // Generate hashtags
  const baseHashtags = '#education #facts #learning #test';
  const topicHashtags = originalData.originalTopic.toLowerCase()
    .split(' ')
    .filter(word => word.length > 2)
    .map(word => '#' + word.replace(/[^a-zA-Z]/g, ''))
    .slice(0, 2)
    .join(' ');

  // Create video metadata
  const videoPackage = {
    title: title || 'Test Educational Content',
    description: `Testing: ${originalData.originalTopic}`,
    script: script,
    cleanScript: script.trim(),
    images: images.map(img => img.url || img.b64_json),
    audioUrl: audioData.url || audioData.b64_json || 'generated',
    audioData: audioData,
    hashtags: `${baseHashtags} ${topicHashtags}`.trim(),
    originalTopic: originalData.originalTopic,
    sourceData: {
      totalPosts: originalData.totalPosts,
      viralPosts: originalData.processedContent.hotPosts.slice(0, 2)
    },
    videoSpecs: {
      duration: '30-45 seconds',
      format: 'vertical 9:16',
      style: 'educational/test',
      voiceover: 'alloy voice'
    },
    generatedAt: new Date().toISOString(),
    readyForEditing: true,
    status: 'success',
    mode: 'testing'
  };

  console.log('Video package created successfully:', JSON.stringify(videoPackage, null, 2));
  return { json: videoPackage };

} catch (error) {
  console.error('Error creating video package:', error);

  return {
    json: {
      title: 'Test Error - Video Package Creation Failed',
      error: error.message,
      status: 'failed',
      originalTopic: $node['Generate Viral Topic'].json.Topic || 'Unknown',
      generatedAt: new Date().toISOString(),
      readyForEditing: false,
      mode: 'testing'
    }
  };
}

// Prepare video creation with FFmpeg command
console.log('=== PREPARING VIDEO CREATION ===');

try {
  const videoData = $node['Create Video Package'].json || {};
  const imageData = $node['Generate Content Images'].json || {};
  const audioData = $node['Generate Audio Narration'].json || {};
  const scriptData = $node['Generate Viral Script'].json || {};

  // Check available assets
  const hasImages = imageData.data && imageData.data.length > 0;
  const hasAudio = audioData.url || audioData.b64_json;
  const hasScript = scriptData.message && scriptData.message.content;

  console.log('Assets available:');
  console.log('- Images:', hasImages, imageData.data?.length || 0);
  console.log('- Audio:', hasAudio);
  console.log('- Script:', hasScript);

  if (!hasImages || !hasAudio) {
    console.log('Missing required assets for video creation');
    return {
      json: {
        status: 'incomplete',
        message: 'Missing required assets for video creation',
        hasImages,
        hasAudio,
        hasScript,
        timestamp: new Date().toISOString()
      }
    };
  }

  // Create video preparation package
  const videoPrep = {
    status: 'ready_for_video_creation',
    title: videoData.title || 'Generated Video',
    assets: {
      images: {
        count: imageData.data.length,
        urls: imageData.data.map(img => img.url),
        firstImage: imageData.data[0]?.url
      },
      audio: {
        url: audioData.url,
        format: 'mp3',
        voice: 'alloy'
      },
      script: {
        content: scriptData.message.content,
        length: scriptData.message.content.length
      }
    },
    videoSpecs: {
      format: '9:16 (1080x1920)',
      duration: '30-45 seconds',
      fps: 30,
      codec: 'H.264',
      quality: 'High'
    },
    ffmpegCommand: {
      description: 'Ready to create video with images, audio, and subtitles',
      command: `ffmpeg -loop 1 -i "${imageData.data[0]?.url}" -i "${audioData.url}" -filter_complex "[0:v]scale=1080:1920:force_original_aspect_ratio=decrease,pad=1080:1920:(ow-iw)/2:(oh-ih)/2[v]" -map "[v]" -map 1:a -c:v libx264 -c:a aac -b:v 2M -b:a 128k -shortest -t 45 output_video.mp4`,
      note: 'Command ready for execution with FFmpeg'
    },
    nextSteps: [
      'Download audio file from URL',
      'Download image files from URLs', 
      'Execute FFmpeg command',
      'Generate final MP4 video'
    ],
    generatedAt: new Date().toISOString()
  };

  console.log('Video preparation complete:', JSON.stringify(videoPrep, null, 2));

  return { json: videoPrep };

} catch (error) {
  console.error('Error preparing video:', error);
  return {
    json: {
      status: 'error',
      message: 'Failed to prepare video creation',
      error: error.message,
      timestamp: new Date().toISOString()
    }
  };
}

// Create final video output with comprehensive metadata
console.log('=== FINAL OUTPUT GENERATION ===');

try {
  const videoData = $node['Create Video Package'].json || {};
  const marketingData = $node['Generate Marketing Copy'].json || {};
  const originalData = $node['Process Viral Content'].json || {};
  const scriptData = $node['Generate Viral Script'].json || {};
  const imageData = $node['Generate Content Images'].json || {};
  const audioData = $node['Generate Audio Narration'].json || {};

  console.log('Video data keys:', Object.keys(videoData));
  console.log('Marketing data keys:', Object.keys(marketingData));
  console.log('Image data keys:', Object.keys(imageData));
  console.log('Audio data keys:', Object.keys(audioData));

  // Video-focused final output
  const finalVideoOutput = {
    videoMetadata: {
      title: videoData.title || 'Generated Video',
      description: videoData.description || 'AI-generated video content',
      topic: originalData.originalTopic || 'Unknown topic',
      duration: videoData.videoSpecs?.duration || '30-45 seconds',
      format: videoData.videoSpecs?.format || '9:16 vertical',
      style: videoData.videoSpecs?.style || 'educational',
      status: videoData.status || 'unknown',
      readyForEditing: videoData.readyForEditing || false,
      generatedAt: new Date().toISOString()
    },
    content: {
      script: {
        fullText: scriptData.message?.content || videoData.script || 'No script available',
        cleanText: videoData.cleanScript || 'No clean script',
        wordCount: (scriptData.message?.content || '').split(' ').length
      },
      audio: {
        available: !!audioData.url || !!audioData.b64_json,
        url: audioData.url || 'Generated audio data',
        format: 'mp3',
        voice: 'alloy',
        instructions: 'Clear, engaging educational voice'
      },
      images: {
        available: !!(imageData.data && imageData.data.length > 0),
        count: imageData.data?.length || 0,
        urls: imageData.data?.map(img => img.url || img.b64_json) || [],
        description: 'Content-relevant images generated'
      },
      marketing: {
        copy: marketingData.message?.content || 'Marketing copy generated',
        hashtags: videoData.hashtags || '#video #content',
        platforms: ['YouTube', 'TikTok', 'Instagram']
      }
    },
    production: {
      sourceData: {
        totalPosts: originalData.totalPosts || 0,
        viralPosts: originalData.processedContent?.hotPosts?.slice(0, 2) || [],
        comments: originalData.processedContent?.viralComments?.slice(0, 3) || []
      },
      assets: {
        hasScript: !!(scriptData.message?.content || videoData.script),
        hasAudio: !!(audioData.url || audioData.b64_json),
        hasImages: !!(imageData.data && imageData.data.length > 0),
        hasMarketing: !!(marketingData.message?.content)
      },
      readyForProduction: videoData.readyForEditing && !!(scriptData.message?.content) && !!(audioData.url || audioData.b64_json)
    }
  };

  console.log('=== FINAL VIDEO OUTPUT COMPLETE ===');
  console.log(JSON.stringify(finalVideoOutput, null, 2));

  return { json: finalVideoOutput };

} catch (error) {
  console.error('Error in final output:', error);
  return {
    json: {
      error: 'Failed to create final output',
      message: error.message,
      timestamp: new Date().toISOString()
    }
  };
}
