// Prepare Reddit posts for OpenAI processing
const posts = $input.all();
const category = $('Configuration').first().json.category;
const topic = $('Configuration').first().json.topic;

// Extract relevant post data
const processedPosts = posts.map(item => {
  const post = item.json.raw;
  if (!post) return null;

  // Get top comments if available
  const topComments = post.comments ? 
    post.comments
      .sort((a, b) => b.score - a.score)
      .slice(0, 5)
      .map(c => ({
        text: c.text,
        score: c.score
      })) : [];

  return {
    title: post.title,
    description: post.description,
    score: post.score,
    subreddit: post.subredditName,
    topComments: topComments
  };
}).filter(Boolean);

return [{
  json: {
    category: category,
    topic: topic,
    posts: processedPosts,
    postsFormatted: JSON.stringify(processedPosts, null, 2)
  }
}];

// Format the generated content for output
const inputData = $input.item.json;
let contents = [];

// Parse the JSON string from OpenAI response
try {
  const contentString = inputData.message?.content;
  if (typeof contentString === 'string') {
    const parsed = JSON.parse(contentString);
    contents = parsed.contents || [];
  }
} catch (e) {
  console.error('Failed to parse content:', e);
  throw new Error('Failed to parse OpenAI response: ' + e.message);
}

const category = $('Configuration').first().json.category;
const topic = $('Configuration').first().json.topic;

// Check if we have valid content
if (!contents || contents.length === 0) {
  throw new Error('No content generated from OpenAI. Response structure: ' + JSON.stringify(inputData));
}

// Return format for n8n Code node with multiple items
return contents.map((item, index) => ({
  json: {
    contentId: `${category}_${topic}_${index + 1}`,
    category: category,
    topic: topic,
    title: item.title || 'Untitled',
    script: `${item.hook || ''}\n\n${item.body || ''}\n\n${item.ending || ''}`.trim(),
    hook: item.hook || '',
    body: item.body || '',
    ending: item.ending || '',
    estimatedDuration: Math.round((item.body || '').split(' ').length / 3), // Rough estimate: 3 words per second
    createdAt: new Date().toISOString()
  }
}));
